// Gallery.js
import React from 'react';

function Gallery() {
  // 12 foto urut dari 1.jpeg sampai 12.jpeg
  const images = Array.from({length: 12}, (_, i) => `/assets/images/${i+1}.jpeg`);
  // Daftar nama kenangan
  const captions = [
    'Kenangan 1', 'Kenangan 2', 'Kenangan 3', 'Kenangan 4', 'Kenangan 5', 'Kenangan 6',
    'Kenangan 7', 'Kenangan 8', 'Kenangan 9', 'Kenangan 10', 'Kenangan 11', 'Kenangan 12',
  ];
  return (
    <section style={{
      background: 'none',
      padding: '2.5rem 0 1.5rem 0',
      margin: '2.5rem auto 1.5rem auto',
      maxWidth: 1100,
      color: '#232946',
      textAlign: 'center',
    }}>
      <h2 style={{
        fontSize: '2.1rem',
        fontWeight: 800,
        marginBottom: 18,
        color: '#232946',
        letterSpacing: '-1px',
        textShadow: '0 2px 12px #b8c6db44',
      }}>Galeri Kenangan</h2>
      <div
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(4, 1fr)',
          gap: 28,
          justifyItems: 'center',
          margin: '32px auto 0 auto',
          maxWidth: 1000,
        }}
      >
        {images.map((src, i) => (
          <div key={i} style={{width: '100%', textAlign: 'center'}}>
            <img
              src={src}
              alt={captions[i]}
              style={{
                width: '100%',
                maxWidth: 180,
                aspectRatio: '1/1',
                objectFit: 'cover',
                borderRadius: 18,
                background: '#e0e7ef',
                boxShadow: '0 4px 24px #b8c6db55, 0 1.5px 8px #23294622',
                marginBottom: 8,
                transition: 'transform 0.18s, box-shadow 0.18s',
                cursor: 'pointer',
              }}
              onMouseOver={e => {
                e.currentTarget.style.transform = 'scale(1.045)';
                e.currentTarget.style.boxShadow = '0 8px 32px #6dd5ed55, 0 2px 12px #23294633';
              }}
              onMouseOut={e => {
                e.currentTarget.style.transform = 'scale(1)';
                e.currentTarget.style.boxShadow = '0 4px 24px #b8c6db55, 0 1.5px 8px #23294622';
              }}
            />
            <div style={{color: '#6dd5ed', fontSize: 15, marginTop: 4, fontWeight: 600, letterSpacing: '-0.2px', textShadow: '0 1px 6px #b8c6db33'}}>{captions[i]}</div>
          </div>
        ))}
      </div>
    </section>
  );
}

export default Gallery;
