// Playlist.js

import React, { useRef, useState } from 'react';
import { FaMusic } from 'react-icons/fa';

function Playlist() {
  // Ganti dengan lagu-lagu favoritmu
  const songs = [
    { title: 'Halstage - terima kasih', url: '/assets/music/Halstage-terima kasih.mp3', favorite: true },
    { title: 'Luthfi Aulia - Langit Favorit', url: '/assets/music/Luthfi Aulia - Langit Favorit.mp3', favorite: false },
    { title: 'Nadhif Basalamah - bergema sampai selamanya', url: '/assets/music/Nadhif Basalamah - bergema sampai selamanya.mp3', favorite: false },
    { title: 'Pamungkas - Monolog', url: '/assets/music/Pamungkas - Monolog.mp3', favorite: false },
    { title: 'Virgoun – Orang Yang Sama', url: '/assets/music/Virgoun – Orang Yang Sama.mp3', favorite: false },
  ];

  const audioRefs = useRef([]);
  const [currentPlaying, setCurrentPlaying] = useState(null);

  const handlePlay = (idx) => {
    audioRefs.current.forEach((audio, i) => {
      if (audio && i !== idx) {
        audio.pause();
        audio.currentTime = 0;
      }
    });
    setCurrentPlaying(idx);
  };
  return (
    <section className="playlist-section">
      {/* Judul lama dihapus agar tidak dobel */}
        <h2 className="playlist-header">My Special Music Moments</h2>
      <div className="playlist-list">
        {songs.map((s, i) => (
          <div className="playlist-card animated-card" key={i} style={{ animationDelay: `${i * 0.15}s` }}>
            <div className="playlist-title">
              <span className="icon-music-wrapper">
                <FaMusic className="playlist-icon" />
              </span>
              <span>{s.title}</span>
              {s.favorite && <span className="badge-favorite">FAVORITE</span>}
            </div>
            <audio
              controls
              src={s.url}
              className="playlist-audio"
              ref={el => audioRefs.current[i] = el}
              onPlay={() => handlePlay(i)}
            />
            <div className="playlist-visualizer">
              <div className="bar"></div>
              <div className="bar"></div>
              <div className="bar"></div>
              <div className="bar"></div>
              <div className="bar"></div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}

export default Playlist;
