// LandingPage.js
import React from 'react';

function LandingPage({ onScrollToContent }) {
  return (
    <section style={{
      minHeight: '100vh',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      background: 'linear-gradient(135deg, #23272f 60%, #2ecc40 100%)',
      color: '#fff',
      textAlign: 'center',
      padding: '0 1rem'
    }}>
      <h1 style={{ fontSize: '2.8rem', marginBottom: 16 }}>Selamat Tahun Baru 2026!</h1>
      <p style={{ fontSize: '1.2rem', marginBottom: 32 }}>
        Semoga tahun ini penuh kebahagiaan, petualangan, dan momen berkesan untukmu!
      </p>
      <button
        onClick={onScrollToContent}
        style={{
          padding: '12px 32px',
          fontSize: '1.1rem',
          borderRadius: 24,
          background: '#fff',
          color: '#2ecc40',
          border: 'none',
          fontWeight: 'bold',
          cursor: 'pointer',
          boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
        }}
      >
        Lihat Selengkapnya â†“
      </button>
    </section>
  );
}

export default LandingPage;
