// LandingPage.js
import React from 'react';

function LandingPage({ onScrollToContent }) {
  // Responsif: atur style berdasarkan lebar layar
  const [isMobile, setIsMobile] = React.useState(window.innerWidth < 600);
  React.useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 600);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <section
      style={{
        minHeight: '100vh',
        width: '100vw',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        background: isMobile
          ? 'linear-gradient(135deg, #232526 60%, #6dd5ed 100%)'
          : 'radial-gradient(circle at 60% 80%, #6dd5ed 40%, #232526 100%)',
        color: '#fff',
        textAlign: 'center',
        padding: isMobile ? '0 0.5rem' : '0 1rem',
        borderRadius: 0,
        margin: 0,
        maxWidth: '100vw',
        boxShadow: 'none',
        overflow: 'hidden',
      }}
    >
      <h1 style={{ fontSize: isMobile ? '2.2rem' : '3.2rem', marginBottom: 18, fontWeight: 700, letterSpacing: '-1px' }}>
        Selamat Tahun Baru 2026!
      </h1>
      <p style={{ fontSize: isMobile ? '1.1rem' : '1.4rem', marginBottom: 36, maxWidth: 600 }}>
        Semoga tahun ini penuh kebahagiaan, petualangan, dan momen berkesan untukmu!
      </p>
      <button
        onClick={onScrollToContent}
        style={{
          padding: isMobile ? '12px 22px' : '14px 38px',
          fontSize: isMobile ? '1.05rem' : '1.2rem',
          borderRadius: 24,
          background: '#fff',
          color: '#2ecc40',
          border: 'none',
          fontWeight: 'bold',
          cursor: 'pointer',
          boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
        }}
      >
        Lihat Selengkapnya â†“
      </button>
    </section>
  );
}

export default LandingPage;
