// LandingPage.js
import React from 'react';


function LandingPage({ onScrollToContent }) {
  // Responsif: atur style berdasarkan lebar layar
  const [isMobile, setIsMobile] = React.useState(window.innerWidth < 600);
  React.useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 600);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <section
      style={{
        minHeight: '100vh',
        width: '100vw',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        background: isMobile
          ? 'linear-gradient(135deg, #e0e7ef 0%, #f7f6f2 60%, #b8c6db 100%)'
          : 'linear-gradient(135deg, #e0e7ef 0%, #f7f6f2 60%, #b8c6db 100%)',
        color: '#232946',
        textAlign: 'center',
        padding: isMobile ? '2.5rem 1rem' : '4.5rem 2.5rem',
        borderRadius: 24,
        margin: isMobile ? '0.5rem' : '1.5rem auto',
        maxWidth: isMobile ? '98vw' : '600px',
        boxShadow: '0 4px 32px 0 #b8c6db33',
        overflow: 'hidden',
        transition: 'background 0.6s',
      }}
    >
      <h1 style={{ fontSize: isMobile ? '2.2rem' : '3.2rem', marginBottom: 10, fontWeight: 700, letterSpacing: '-1px', color: '#232946' }}>
        Selamat Tahun Baru 2026
      </h1>
      <h2 style={{ fontSize: isMobile ? '1.25rem' : '1.7rem', fontWeight: 600, color: '#52616b', marginBottom: 18, letterSpacing: '-0.5px' }}>
        Awali Tahun dengan Semangat Baru
      </h2>
      <p style={{ fontSize: isMobile ? '1.08rem' : '1.22rem', marginBottom: 36, maxWidth: 600, color: '#393e46', lineHeight: 1.6 }}>
        Tahun baru, lembaran baru. Semoga 2026 membawa banyak inspirasi, pengalaman seru, dan pencapaian baru untuk kita semua. Jangan lupa untuk terus mencoba hal-hal baru, berbagi tawa, dan menikmati setiap prosesnya. <br />
        <span style={{ color: '#8899a6', fontSize: isMobile ? 13 : 15 }}>Cheers to new adventures and good vibes!</span>
      </p>
      <button
        onClick={onScrollToContent}
        style={{
          padding: isMobile ? '12px 28px' : '16px 48px',
          fontSize: isMobile ? '1.08rem' : '1.22rem',
          borderRadius: 32,
          background: 'linear-gradient(90deg, #b8c6db 0%, #f7f6f2 100%)',
          color: '#232946',
          border: 'none',
          fontWeight: 700,
          cursor: 'pointer',
          boxShadow: '0 2px 12px 0 #b8c6db44',
          transition: 'background 0.2s, color 0.2s',
          marginTop: 10,
        }}
        onMouseOver={e => {
          e.target.style.background = '#e0e7ef';
          e.target.style.color = '#393e46';
        }}
        onMouseOut={e => {
          e.target.style.background = 'linear-gradient(90deg, #b8c6db 0%, #f7f6f2 100%)';
          e.target.style.color = '#232946';
        }}
      >
        Mulai
      </button>
    </section>
  );
}

export default LandingPage;
