// LandingPage.js
import React from 'react';


function LandingPage({ onScrollToContent }) {
  // Responsif: atur style berdasarkan lebar layar
  const [isMobile, setIsMobile] = React.useState(window.innerWidth < 600);
  React.useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 600);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Animasi teks sederhana
  const [showText, setShowText] = React.useState(false);
  React.useEffect(() => {
    const timer = setTimeout(() => setShowText(true), 600);
    return () => clearTimeout(timer);
  }, []);

  return (
    <section
      style={{
        minHeight: '100vh',
        width: '100vw',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        background: isMobile
          ? 'radial-gradient(circle at 60% 40%, #a18cd1 0%, #2a5298 60%, #1e3c72 100%)'
          : 'radial-gradient(circle at 60% 40%, #a18cd1 0%, #2a5298 60%, #1e3c72 100%)',
        color: '#fff',
        textAlign: 'center',
        padding: isMobile ? '0 0.5rem' : '0 1rem',
        borderRadius: 0,
        margin: 0,
        maxWidth: '100vw',
        boxShadow: 'none',
        overflow: 'hidden',
        position: 'relative',
      }}
    >
      {/* Emoji kembang api animasi */}
      <div style={{ fontSize: isMobile ? 38 : 60, marginBottom: 8, animation: 'pop 1.2s' }}>
        ðŸŽ†âœ¨ðŸŽ‡
      </div>
      <h1
        style={{
          fontSize: isMobile ? '2.3rem' : '3.3rem',
          marginBottom: 18,
          fontWeight: 700,
          letterSpacing: '-1px',
          opacity: showText ? 1 : 0,
          transform: showText ? 'translateY(0)' : 'translateY(30px)',
          transition: 'all 1s cubic-bezier(.6,-0.28,.74,.05)',
        }}
      >
        Selamat Tahun Baru 2026, <br />
        <span
          style={{
            color: '#fff',
            fontWeight: 800,
            textShadow: '0 2px 8px rgba(30,60,114,0.18), 0 1px 0 #6dd5ed',
            background: 'linear-gradient(90deg, #f7971e 0%, #ffd200 100%)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            display: 'inline-block',
            padding: '0 6px',
          }}
        >
          Redafyn Danu Mahardika ðŸ’™
        </span>
      </h1>
      <p
        style={{
          fontSize: isMobile ? '1.13rem' : '1.35rem',
          marginBottom: 30,
          maxWidth: 600,
          opacity: showText ? 1 : 0,
          transition: 'opacity 1.2s 0.5s',
        }}
      >
        "Terima kasih sudah jadi bagian terindah di hidupku. Semoga tahun ini membawa lebih banyak tawa, petualangan, dan cinta untuk kita berdua."
        <br />
        <span style={{ fontSize: isMobile ? 18 : 22, color: '#ffd200', fontWeight: 600 }}>Aku sayang kamu! ðŸ’›</span>
      </p>
      <button
        onClick={onScrollToContent}
        style={{
          padding: isMobile ? '12px 22px' : '14px 38px',
          fontSize: isMobile ? '1.05rem' : '1.2rem',
          borderRadius: 24,
          background: '#fff',
          color: '#1e3c72',
          border: 'none',
          fontWeight: 'bold',
          cursor: 'pointer',
          boxShadow: '0 2px 8px rgba(0,0,0,0.12)',
          transition: 'background 0.2s, color 0.2s',
        }}
        onMouseOver={e => {
          e.target.style.background = '#1e3c72';
          e.target.style.color = '#fff';
        }}
        onMouseOut={e => {
          e.target.style.background = '#fff';
          e.target.style.color = '#1e3c72';
        }}
      >
        Mulai Petualangan Baru
      </button>
      {/* Animasi keyframes sederhana */}
      <style>{`
        @keyframes pop {
          0% { transform: scale(0.7); opacity: 0; }
          60% { transform: scale(1.2); opacity: 1; }
          100% { transform: scale(1); opacity: 1; }
        }
      `}</style>
    </section>
  );
}

export default LandingPage;
