// App.js
import React from 'react';

import './styles/main.css';

import LandingPage from './components/LandingPage';
import Fireworks from './components/Fireworks';
import FireworksJourney from './components/FireworksJourney';
import Greeting from './components/Greeting';
import Timeline from './components/Timeline';
import FutureLetter from './components/FutureLetter';
import Gallery from './components/Gallery';
import Playlist from './components/Playlist';
import InspirationalQuote from './components/InspirationalQuote';
import FunFact from './components/FunFact';

import ParticleBackground from './components/ParticleBackground';


function App() {
  const contentRef = React.useRef(null);
  const [showContent, setShowContent] = React.useState(false);

  const handleScrollToContent = () => {
    setShowContent(true);
    setTimeout(() => {
      if (contentRef.current) {
        contentRef.current.scrollIntoView({ behavior: 'smooth' });
      }
    }, 100); // delay for fade-in
  };

  return (
    <div className="App" style={{position: 'relative', minHeight: '100vh', overflow: 'hidden'}}>
      <ParticleBackground />
      <Fireworks />
      <LandingPage onScrollToContent={handleScrollToContent} />
      <div
        ref={contentRef}
        className={`fade-content${showContent ? ' show' : ''}`}
        style={{
          padding: '2rem 0',
          background: '#181c20',
          position: 'relative',
          zIndex: 2,
          minHeight: '100vh',
          transition: 'opacity 0.7s',
          opacity: showContent ? 1 : 0,
          pointerEvents: showContent ? 'auto' : 'none',
        }}
      >
        <InspirationalQuote />
        <FunFact />
        <FireworksJourney />
        <div className="main-container" style={{position: 'relative', zIndex: 2}}>
          <div style={{marginBottom: '-0.7rem'}}>
            <span style={{fontSize: '2.5rem', display: 'block'}}>üéâ</span>
          </div>
          <h2>Your 2025 Journey</h2>
          <div style={{fontSize: '1.1rem', color: '#b5eaff', margin: '0.3rem 0 0.7rem 0', fontStyle: 'italic', fontWeight: 400}}>
            Setiap momen adalah kenangan, setiap langkah adalah harapan.
          </div>
          <div style={{margin: '1.5rem 0 2.2rem 0'}}>
            <div style={{
              fontSize: '1.18rem',
              color: '#e3e3e3',
              fontStyle: 'italic',
              margin: '0 0 1.3rem 0',
              lineHeight: 1.6,
              textAlign: 'center',
              maxWidth: 520,
              marginLeft: 'auto',
              marginRight: 'auto',
              marginTop: 0,
            }}>
              <div>‚ÄúHidup adalah kumpulan momen kecil yang layak dirayakan.‚Äù</div>
              <div>‚ÄúJangan takut melangkah, setiap langkah adalah cerita.‚Äù</div>
              <div>‚ÄúSemoga tahun ini membawa lebih banyak tawa dan harapan.‚Äù</div>
            </div>
            <div style={{
              fontSize: '1.1rem',
              color: '#b5eaff',
              margin: '1.7rem 0 2.2rem 0',
              fontStyle: 'italic',
              fontWeight: 400,
              textAlign: 'center',
              opacity: 1,
            }}>
              Terima kasih sudah menjadi bagian dari perjalanan ini.
            </div>
            <div style={{width: 80, height: 3, background: 'linear-gradient(90deg, #6dd5ed, #a18cd1)', borderRadius: 2, opacity: 0.5, margin: '2.2rem auto 2.2rem auto'}} />
          </div>
          {/* <FutureLetter /> */}
          <Gallery />
          <Playlist />
        </div>
      </div>
    </div>
  );
}

export default App;
