// Gallery.js
import React from 'react';

function Gallery() {
  // 12 foto urut dari 1.jpeg sampai 12.jpeg
  const images = Array.from({length: 12}, (_, i) => `/assets/images/${i+1}.jpeg`);
  // Daftar nama kenangan
  const captions = [
    'Kenangan 1', 'Kenangan 2', 'Kenangan 3', 'Kenangan 4', 'Kenangan 5', 'Kenangan 6',
    'Kenangan 7', 'Kenangan 8', 'Kenangan 9', 'Kenangan 10', 'Kenangan 11', 'Kenangan 12',
  ];
  return (
    <section style={{
      background: 'linear-gradient(135deg, #f7f6f2 0%, #e0e7ef 60%, #b8c6db 100%)',
      borderRadius: 32,
      padding: '2.8rem 1.5rem',
      margin: '2.5rem auto',
      maxWidth: 900,
      boxShadow: '0 8px 40px 0 #b8c6db44',
      color: '#232946',
      textAlign: 'center',
      border: '1.5px solid #e0e7ef',
      backgroundBlendMode: 'overlay',
      transition: 'background 0.6s',
    }}>
      <h2 style={{
        fontSize: '2.3rem',
        fontWeight: 800,
        marginBottom: 18,
        background: 'linear-gradient(90deg, #e0e7ef 30%, #b8c6db 70%)',
        WebkitBackgroundClip: 'text',
        WebkitTextFillColor: 'transparent',
        letterSpacing: '-1px',
        textShadow: '0 4px 24px #23294699',
      }}>Galeri Kenangan</h2>
      <div
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(4, 1fr)',
          gap: 24,
          justifyItems: 'center',
          margin: '32px auto 0 auto',
          maxWidth: 900,
        }}
      >
        {images.map((src, i) => (
          <div key={i} style={{width: '100%', textAlign: 'center'}}>
            <img
              src={src}
              alt={captions[i]}
              style={{
                width: '100%',
                maxWidth: 180,
                aspectRatio: '1/1',
                objectFit: 'cover',
                borderRadius: 12,
                background: '#232946',
                boxShadow: '0 2px 12px #23294633',
                marginBottom: 10
              }}
            />
            <div style={{color: '#b8c6db', fontSize: 15, marginTop: 4, fontWeight: 500}}>{captions[i]}</div>
          </div>
        ))}
      </div>
    </section>
  );
}

export default Gallery;
