import React, { useEffect, useRef } from 'react';

function randomBetween(a, b) {
  return a + Math.random() * (b - a);
}

function SectionFireworks({ height = 420 }) {
  const canvasRef = useRef();
  const animationRef = useRef();

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let dpr = window.devicePixelRatio || 1;
    let width = canvas.parentElement.offsetWidth;
    canvas.width = width * dpr;
    canvas.height = height * dpr;
    canvas.style.width = width + 'px';
    canvas.style.height = height + 'px';
    ctx.setTransform(dpr, 0, 0, dpr, 0, 0);

    let fireworks = [];
    let particles = [];

    function createFirework() {
      const x = randomBetween(width * 0.15, width * 0.85);
      const y = randomBetween(height * 0.18, height * 0.45);
      // Kombinasi warna elegan, cerah, dan harmonis
      const colorList = [
        '#6dd5ed', // biru muda
        '#2193b0', // biru gelap
        '#a18cd1', // ungu pastel
        '#f7971e', // oranye gold
        '#ffd200', // kuning gold
        '#fff',    // putih
        '#ff6f91', // pink cerah
        '#00c3ff', // biru neon
        '#f9ea8f', // kuning pastel
        '#b388ff', // ungu neon
        '#43e97b', // hijau toska
        '#38f9d7', // hijau muda
        '#ffb199', // peach
        '#f953c6', // pink magenta
      ];
      const color = colorList[Math.floor(Math.random() * colorList.length)];
      fireworks.push({ x, y, color, radius: 0, maxRadius: randomBetween(60, 120) });
    }

    function createParticles(firework) {
      const count = Math.floor(randomBetween(36, 60));
      for (let i = 0; i < count; i++) {
        const angle = (Math.PI * 2 * i) / count;
        const speed = randomBetween(2, 5);
        particles.push({
          x: firework.x,
          y: firework.y,
          vx: Math.cos(angle) * speed,
          vy: Math.sin(angle) * speed,
          alpha: 1,
          color: firework.color,
        });
      }
    }

    let lastFirework = 0;
    function animate(ts) {
      ctx.clearRect(0, 0, width, height);
      // Draw fireworks
      for (let i = fireworks.length - 1; i >= 0; i--) {
        let fw = fireworks[i];
        ctx.beginPath();
        ctx.arc(fw.x, fw.y, fw.radius, 0, Math.PI * 2);
        ctx.strokeStyle = fw.color;
        ctx.lineWidth = 2;
        ctx.globalAlpha = 0.8;
        ctx.stroke();
        ctx.globalAlpha = 1;
        fw.radius += 3;
        if (fw.radius > fw.maxRadius) {
          createParticles(fw);
          fireworks.splice(i, 1);
        }
      }
      // Draw particles
      for (let i = particles.length - 1; i >= 0; i--) {
        let p = particles[i];
        ctx.beginPath();
        ctx.arc(p.x, p.y, 2, 0, Math.PI * 2);
        ctx.fillStyle = p.color;
        ctx.globalAlpha = p.alpha;
        ctx.fill();
        ctx.globalAlpha = 1;
        p.x += p.vx;
        p.y += p.vy;
        p.vy += 0.04;
        p.alpha -= 0.012;
        if (p.alpha <= 0) particles.splice(i, 1);
      }
      // Launch more fireworks in this section (5-7x per detik)
      if (!lastFirework || ts - lastFirework > 180) {
        for (let i = 0; i < Math.floor(randomBetween(2, 4)); i++) {
          createFirework();
        }
        lastFirework = ts;
      }
      animationRef.current = requestAnimationFrame(animate);
    }
    animationRef.current = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(animationRef.current);
  }, [height]);

  return (
    <canvas
      ref={canvasRef}
      style={{
        position: 'absolute',
        left: 0,
        top: 0,
        width: '100%',
        height: height + 'px',
        pointerEvents: 'none',
        zIndex: 1,
      }}
    />
  );
}

export default SectionFireworks;
