// LandingPage.js
import React from 'react';


function LandingPage({ onScrollToContent }) {
  // Responsif: atur style berdasarkan lebar layar
  const [isMobile, setIsMobile] = React.useState(window.innerWidth < 600);
  React.useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 600);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <section
      style={{
        minHeight: '100vh',
        width: '100vw',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        background: 'none',
        color: '#e0e7ef',
        textAlign: 'center',
        padding: isMobile ? '2.5rem 1rem' : '4.5rem 2.5rem',
        borderRadius: 0,
        margin: 0,
        maxWidth: '100vw',
        boxShadow: 'none',
        overflow: 'hidden',
        transition: 'background 0.6s',
      }}
    >
      <h1 style={{ fontSize: isMobile ? '2.2rem' : '3.2rem', marginBottom: 18, fontWeight: 700, letterSpacing: '-1px', color: '#e0e7ef', textShadow: '0 2px 8px #23294655' }}>
        Selamat Tahun Baru 2026, <br />
        <span style={{
          color: '#e0e7ef',
          fontWeight: 800,
          textShadow: '0 2px 8px #23294699, 0 1px 0 #b8c6db',
        }}>
          Redafyn Danu Mahardika!
        </span>
      </h1>
      <p style={{ fontSize: isMobile ? '1.1rem' : '1.4rem', marginBottom: 36, maxWidth: 600, color: '#e0e7ef', textShadow: '0 1px 6px #23294688' }}>
        Semoga tahun ini penuh kebahagiaan, petualangan, dan momen berkesan untukmu!
      </p>
      <button
        onClick={onScrollToContent}
        style={{
          padding: isMobile ? '12px 28px' : '16px 48px',
          fontSize: isMobile ? '1.08rem' : '1.22rem',
          borderRadius: 32,
          background: 'rgba(224,231,239,0.18)',
          color: '#e0e7ef',
          border: '1.5px solid #e0e7ef55',
          fontWeight: 700,
          cursor: 'pointer',
          boxShadow: '0 2px 12px 0 #23294622',
          transition: 'background 0.2s, color 0.2s',
          marginTop: 10,
        }}
        onMouseOver={e => {
          e.target.style.background = '#232946';
          e.target.style.color = '#e0e7ef';
        }}
        onMouseOut={e => {
          e.target.style.background = 'rgba(224,231,239,0.18)';
          e.target.style.color = '#e0e7ef';
        }}
      >
        Mulai
      </button>

    </section>
  );
}

export default LandingPage;
