// FutureLetter.js
import React, { useState } from 'react';

function FutureLetter() {
  const [message, setMessage] = useState('');
  const [saved, setSaved] = useState(false);

  const handleSave = () => {
    localStorage.setItem('futureLetter', message);
    setSaved(true);
  };

  return (
    <section style={{margin: '2rem 0'}}>
      <h2>Surat untuk Masa Depan</h2>
      <textarea
        rows={5}
        style={{width: '100%', maxWidth: 400, borderRadius: 8, padding: 8}}
        placeholder="Tulis pesan untuk dirimu di masa depan..."
        value={message}
        onChange={e => setMessage(e.target.value)}
      />
      <br />
      <button onClick={handleSave} style={{marginTop: 8, padding: '8px 16px', borderRadius: 6, background: '#2ecc40', color: '#fff', border: 'none'}}>
        Simpan Surat
      </button>
      {saved && <div style={{color: '#2ecc40', marginTop: 8}}>Surat tersimpan! Buka tahun depan ya :)</div>}
    </section>
  );
}

export default FutureLetter;
