import React from 'react';

const playlist = [
  {
    title: 'Avicii - The Nights',
    url: 'https://www.youtube.com/watch?v=UtF6Jej8yb4',
    platform: 'YouTube',
  },
  {
    title: 'OneRepublic - Good Life',
    url: 'https://www.youtube.com/watch?v=jZhQOvvV45w',
    platform: 'YouTube',
  },
  {
    title: 'Coldplay - Adventure Of A Lifetime',
    url: 'https://www.youtube.com/watch?v=QtXby3twMmI',
    platform: 'YouTube',
  },
  {
    title: 'Tulus - Monokrom',
    url: 'https://www.youtube.com/watch?v=5zwfA9jR7yE',
    platform: 'YouTube',
  },
  {
    title: 'Hivi! - Remaja',
    url: 'https://www.youtube.com/watch?v=Qv7wQb1g1nA',
    platform: 'YouTube',
  },
  {
    title: 'Pamungkas - To The Bone',
    url: 'https://www.youtube.com/watch?v=5zwfA9jR7yE',
    platform: 'YouTube',
  },
  {
    title: 'Taylor Swift - New Year’s Day',
    url: 'https://www.youtube.com/watch?v=jA0cWaaTEn4',
    platform: 'YouTube',
  },
];

export default function PlaylistSection() {
  return (
    <section
      id="playlist-section"
      style={{
        minHeight: '40vh',
        width: '100%',
        background: 'none',
        color: '#e0e7ef',
        textAlign: 'center',
        padding: 'clamp(32px, 8vw, 80px) clamp(8px, 2vw, 24px)',
        boxSizing: 'border-box',
        borderTop: '1.5px solid #b8c6db33',
        zIndex: 2,
      }}
    >
      <h2 style={{
        fontSize: 'clamp(1.2rem, 4vw, 2rem)',
        fontWeight: 700,
        marginBottom: 10,
        letterSpacing: '-0.5px',
        background: 'linear-gradient(90deg, #b8c6db 0%, #6dd5ed 100%)',
        WebkitBackgroundClip: 'text',
        WebkitTextFillColor: 'transparent',
        filter: 'drop-shadow(0 2px 8px #23294655)'
      }}>
        Playlist Tahun Baru
      </h2>
      <div style={{
        marginBottom: 22,
        color: '#e0e7efcc',
        fontSize: '1.08rem',
        fontWeight: 400,
        maxWidth: 600,
        marginLeft: 'auto',
        marginRight: 'auto',
        lineHeight: 1.5,
      }}>
        Temani momen tahun barumu dengan lagu-lagu penuh semangat, harapan, dan kenangan. Klik judul lagu untuk mendengarkan!
      </div>
      <ul style={{
        listStyle: 'none',
        padding: 0,
        margin: '0 auto',
        maxWidth: 480,
        display: 'flex',
        flexDirection: 'column',
        gap: '14px',
      }}>
        {playlist.map((song, idx) => (
          <li key={song.title} style={{
            background: 'rgba(184,198,219,0.10)',
            borderRadius: 14,
            padding: '14px 18px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'space-between',
            boxShadow: '0 1px 8px #23294622',
            transition: 'background 0.18s',
          }}
          onMouseOver={e => e.currentTarget.style.background = 'rgba(109,213,237,0.18)'}
          onMouseOut={e => e.currentTarget.style.background = 'rgba(184,198,219,0.10)'}
          >
            <span style={{ fontWeight: 500, fontSize: '1.08rem', textAlign: 'left' }}>{idx + 1}. <a href={song.url} target="_blank" rel="noopener noreferrer" style={{ color: '#6dd5ed', textDecoration: 'none', fontWeight: 700 }}>{song.title}</a></span>
            <span style={{ fontSize: '0.98rem', color: '#b8c6db', marginLeft: 12 }}>{song.platform}</span>
          </li>
        ))}
      </ul>
    </section>
  );
}
