// LandingPage.js
import React from 'react';

function LandingPage({ onScrollToContent }) {
  // Responsif: atur style berdasarkan lebar layar
  const [isMobile, setIsMobile] = React.useState(window.innerWidth < 600);
  React.useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 600);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <section
      style={{
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        background: isMobile
          ? 'linear-gradient(135deg, #23272f 60%, #2ecc40 100%)'
          : 'radial-gradient(circle at 70% 80%, #2ecc40 30%, #23272f 80%)',
        color: '#fff',
        textAlign: 'center',
        padding: isMobile ? '0 0.5rem' : '0 1rem',
        borderRadius: isMobile ? 0 : 32,
        margin: isMobile ? 0 : '2rem auto',
        maxWidth: isMobile ? '100vw' : 700,
        boxShadow: isMobile ? 'none' : '0 2px 24px rgba(44,204,64,0.08)'
      }}
    >
      <h1 style={{ fontSize: isMobile ? '2rem' : '2.8rem', marginBottom: 16 }}>
        Selamat Tahun Baru 2026!
      </h1>
      <p style={{ fontSize: isMobile ? '1rem' : '1.2rem', marginBottom: 32 }}>
        Semoga tahun ini penuh kebahagiaan, petualangan, dan momen berkesan untukmu!
      </p>
      <button
        onClick={onScrollToContent}
        style={{
          padding: isMobile ? '10px 18px' : '12px 32px',
          fontSize: isMobile ? '1rem' : '1.1rem',
          borderRadius: 24,
          background: '#fff',
          color: '#2ecc40',
          border: 'none',
          fontWeight: 'bold',
          cursor: 'pointer',
          boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
        }}
      >
        Lihat Selengkapnya â†“
      </button>
    </section>
  );
}

export default LandingPage;
