// Gallery.js
import React from 'react';

function Gallery() {
  // 12 foto urut dari 1.jpeg sampai 12.jpeg
  const images = Array.from({length: 12}, (_, i) => `/assets/images/${i+1}.jpeg`);
  return (
    <section style={{margin: '2rem 0'}}>
      <h2>Galeri Kenangan</h2>
      <div
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(4, 1fr)',
          gap: 24,
          justifyItems: 'center',
          margin: '32px auto 0 auto',
          maxWidth: 900,
        }}
      >
        {images.map((src, i) => (
          <div key={i} style={{width: '100%', textAlign: 'center'}}>
            <img
              src={src}
              alt={`Kenangan ${i+1}`}
              style={{
                width: '100%',
                maxWidth: 180,
                aspectRatio: '1/1',
                objectFit: 'cover',
                borderRadius: 12,
                background: '#333',
                boxShadow: '0 2px 12px rgba(0,0,0,0.10)',
                marginBottom: 10
              }}
            />
            {/* <div style={{color: '#b5eaff', fontSize: 14, marginTop: 4}}>{`Kenangan ${i+1}`}</div> */}
          </div>
        ))}
      </div>
    </section>
  );
}

export default Gallery;
