// FireworksJourney.js
import React, { useEffect, useRef } from 'react';

function randomBetween(a, b) {
  return a + Math.random() * (b - a);
}

function FireworksJourney() {
  const canvasRef = useRef();
  const animationRef = useRef();

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let dpr = window.devicePixelRatio || 1;
    let width = canvas.offsetWidth;
    let height = canvas.offsetHeight;
    canvas.width = width * dpr;
    canvas.height = height * dpr;
    ctx.setTransform(dpr, 0, 0, dpr, 0, 0);

    let fireworks = [];
    let particles = [];

    function createFirework() {
      const x = randomBetween(width * 0.2, width * 0.8);
      const y = randomBetween(height * 0.1, height * 0.5);
      const colorList = [
        '#6dd5ed', '#2193b0', '#a18cd1', '#f7971e', '#ffd200', '#fff', '#ee9ca7', '#42275a', '#00c3ff', '#ffffcc'
      ];
      const color = colorList[Math.floor(Math.random() * colorList.length)];
      fireworks.push({ x, y, color, radius: 0, maxRadius: randomBetween(30, 60) });
    }

    function createParticles(firework) {
      const count = Math.floor(randomBetween(18, 32));
      for (let i = 0; i < count; i++) {
        const angle = (Math.PI * 2 * i) / count;
        const speed = randomBetween(1, 2.5);
        particles.push({
          x: firework.x,
          y: firework.y,
          vx: Math.cos(angle) * speed,
          vy: Math.sin(angle) * speed,
          alpha: 1,
          color: firework.color,
        });
      }
    }

    let lastFirework = 0;
    function animate(ts) {
      ctx.clearRect(0, 0, width, height);
      for (let i = fireworks.length - 1; i >= 0; i--) {
        let fw = fireworks[i];
        ctx.beginPath();
        ctx.arc(fw.x, fw.y, fw.radius, 0, Math.PI * 2);
        ctx.strokeStyle = fw.color;
        ctx.lineWidth = 2;
        ctx.globalAlpha = 0.7;
        ctx.stroke();
        ctx.globalAlpha = 1;
        fw.radius += 2;
        if (fw.radius > fw.maxRadius) {
          createParticles(fw);
          fireworks.splice(i, 1);
        }
      }
      for (let i = particles.length - 1; i >= 0; i--) {
        let p = particles[i];
        ctx.beginPath();
        ctx.arc(p.x, p.y, 1.5, 0, Math.PI * 2);
        ctx.fillStyle = p.color;
        ctx.globalAlpha = p.alpha;
        ctx.fill();
        ctx.globalAlpha = 1;
        p.x += p.vx;
        p.y += p.vy;
        p.vy += 0.02;
        p.alpha -= 0.018;
        if (p.alpha <= 0) particles.splice(i, 1);
      }
      if (!lastFirework || ts - lastFirework > 1200) {
        createFirework();
        lastFirework = ts;
      }
      animationRef.current = requestAnimationFrame(animate);
    }
    animationRef.current = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(animationRef.current);
  }, []);

  return (
    <canvas
      ref={canvasRef}
      style={{
        position: 'absolute',
        left: 0,
        top: 0,
        width: '100%',
        height: '100%',
        pointerEvents: 'none',
        zIndex: 1,
      }}
    />
  );
}

export default FireworksJourney;
