// LandingPage.js
import React from 'react';


function LandingPage({ onScrollToContent }) {
  // Responsif: atur style berdasarkan lebar layar
  const [isMobile, setIsMobile] = React.useState(window.innerWidth < 600);
  React.useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 600);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <section
      style={{
        minHeight: '100vh',
        width: '100vw',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        background: isMobile
          ? 'linear-gradient(135deg, #e0e7ef 0%, #f7f6f2 60%, #b8c6db 100%)'
          : 'linear-gradient(135deg, #e0e7ef 0%, #f7f6f2 60%, #b8c6db 100%)',
        color: '#232946',
        textAlign: 'center',
        padding: isMobile ? '0 0.5rem' : '0 1rem',
        borderRadius: 0,
        margin: 0,
        maxWidth: '100vw',
        boxShadow: 'none',
        overflow: 'hidden',
        transition: 'background 0.6s',
      }}
    >
      <h1 style={{ fontSize: isMobile ? '2.2rem' : '3.2rem', marginBottom: 18, fontWeight: 700, letterSpacing: '-1px' }}>
        Selamat Tahun Baru 2026, <br />
        <span style={{
          color: '#fff',
          fontWeight: 800,
          textShadow: '0 2px 8px rgba(30,60,114,0.18), 0 1px 0 #6dd5ed'
        }}>
          Redafyn Danu Mahardika!
        </span>
      </h1>
      <p style={{ fontSize: isMobile ? '1.1rem' : '1.4rem', marginBottom: 36, maxWidth: 600 }}>
        Semoga tahun ini penuh kebahagiaan, petualangan, dan momen berkesan untukmu!
      </p>
      <button
        onClick={onScrollToContent}
        style={{
          padding: isMobile ? '12px 22px' : '14px 38px',
          fontSize: isMobile ? '1.05rem' : '1.2rem',
          borderRadius: 24,
          background: '#fff',
          color: '#1e3c72',
          border: 'none',
          fontWeight: 'bold',
          cursor: 'pointer',
          boxShadow: '0 2px 8px rgba(0,0,0,0.12)',
          transition: 'background 0.2s, color 0.2s',
        }}
        onMouseOver={e => {
          e.target.style.background = '#1e3c72';
          e.target.style.color = '#fff';
        }}
        onMouseOut={e => {
          e.target.style.background = '#fff';
          e.target.style.color = '#1e3c72';
        }}
      >
        Get Started
      </button>
    </section>
  );
}

export default LandingPage;
