import React from 'react';

const quotes = [
  'Tahun baru, semangat baru! Jadikan setiap hari berarti.',
  'Jangan takut bermimpi besar di tahun ini.',
  'Setiap langkah kecil hari ini adalah awal dari pencapaian besar esok.',
  'Syukuri yang telah lalu, sambut yang akan datang dengan senyuman.',
  'Tahun baru adalah halaman kosong, tulislah kisah terbaikmu!',
  'Percaya pada dirimu sendiri, tahun ini milikmu!',
  'Jangan menyerah, keajaiban bisa datang kapan saja.',
  'Bersyukur, berjuang, dan terus bermimpi.',
  'Waktu terus berjalan, jangan sia-siakan kesempatan.',
  'Selamat datang tahun baru, selamat datang harapan baru.'
];

function getRandomQuote() {
  return quotes[Math.floor(Math.random() * quotes.length)];
}

const InspirationalQuote = () => {
  const [quote] = React.useState(getRandomQuote());
  return (
    <div className="inspirational-quote animated-glow">
      <span className="icon-motivation" role="img" aria-label="sparkle">✨</span>
      <span className="quote-mark">“</span>
      {quote}
      <span className="quote-mark">”</span>
    </div>
  );
};

export default InspirationalQuote;
