// LandingPage.js
import React from 'react';


function LandingPage({ onScrollToContent }) {
  // Responsif: atur style berdasarkan lebar layar
  const [isMobile, setIsMobile] = React.useState(window.innerWidth < 600);
  React.useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 600);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <section
      style={{
        minHeight: '100vh',
        width: '100vw',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        background: 'none',
        color: '#e0e7ef',
        textAlign: 'center',
        padding: isMobile ? '2.5rem 1rem' : '4.5rem 2.5rem',
        borderRadius: 0,
        margin: 0,
        maxWidth: '100vw',
        boxShadow: 'none',
        overflow: 'hidden',
        transition: 'background 0.6s',
      }}
    >
      <h1 style={{
        fontSize: isMobile ? '2.3rem' : '3.4rem',
        marginBottom: 18,
        fontWeight: 800,
        letterSpacing: '-1.5px',
        color: '#e0e7ef',
        textShadow: '0 4px 24px #23294699, 0 1px 0 #b8c6db',
        lineHeight: 1.13,
        background: 'linear-gradient(90deg, #e0e7ef 30%, #b8c6db 70%)',
        WebkitBackgroundClip: 'text',
        WebkitTextFillColor: 'transparent',
        filter: 'drop-shadow(0 2px 8px #23294655)'
      }}>
        Selamat Tahun Baru 2026,<br />
        <span style={{
          fontWeight: 900,
          background: 'linear-gradient(90deg, #6dd5ed 0%, #b8c6db 100%)',
          WebkitBackgroundClip: 'text',
          WebkitTextFillColor: 'transparent',
          letterSpacing: '-1.5px',
          filter: 'drop-shadow(0 2px 8px #23294655)'
        }}>
          Redafyn Danu Mahardika!
        </span>
      </h1>
      <p style={{
        fontSize: isMobile ? '1.13rem' : '1.35rem',
        marginBottom: 38,
        maxWidth: 600,
        color: '#e0e7ef',
        textShadow: '0 1px 8px #23294688',
        fontWeight: 400,
        letterSpacing: '-0.2px',
        lineHeight: 1.6,
        background: 'rgba(35,41,70,0.18)',
        borderRadius: 12,
        padding: isMobile ? '0.7rem 0.7rem' : '1rem 1.2rem',
        display: 'inline-block',
        boxShadow: '0 2px 12px 0 #23294622',
      }}>
        Semoga tahun ini penuh kebahagiaan, petualangan, dan momen berkesan untukmu!
      </p>
      <button
        onClick={onScrollToContent}
        style={{
          padding: isMobile ? '13px 34px' : '17px 56px',
          fontSize: isMobile ? '1.13rem' : '1.28rem',
          borderRadius: 32,
          background: 'rgba(224,231,239,0.18)',
          color: '#e0e7ef',
          border: '1.5px solid #b8c6db55',
          fontWeight: 700,
          cursor: 'pointer',
          boxShadow: '0 4px 24px 0 #23294644',
          transition: 'all 0.22s cubic-bezier(.4,0,.2,1)',
          marginTop: 10,
          backdropFilter: 'blur(6px)',
          WebkitBackdropFilter: 'blur(6px)',
          letterSpacing: '-0.5px',
        }}
        onMouseOver={e => {
          e.target.style.background = 'rgba(184,198,219,0.32)';
          e.target.style.color = '#232946';
          e.target.style.boxShadow = '0 6px 32px 0 #6dd5ed55';
        }}
        onMouseOut={e => {
          e.target.style.background = 'rgba(224,231,239,0.18)';
          e.target.style.color = '#e0e7ef';
          e.target.style.boxShadow = '0 4px 24px 0 #23294644';
        }}
      >
        Mulai
      </button>

    </section>
  );
}

export default LandingPage;
