import React, { useRef, useState, useEffect } from 'react';
import { FaVolumeUp, FaVolumeMute } from 'react-icons/fa';

// Ganti dengan file musik yang kamu inginkan di public/assets/music
const MUSIC_URL = '/assets/music/Nadhif Basalamah - bergema sampai selamanya.mp3';

function BackgroundMusic() {
  const audioRef = useRef(null);
  const [muted, setMuted] = useState(false);
  const [playing, setPlaying] = useState(false);

  useEffect(() => {
    // Autoplay saat halaman dibuka
    if (audioRef.current && !playing) {
      audioRef.current.volume = 0.5;
      const playPromise = audioRef.current.play();
      if (playPromise !== undefined) {
        playPromise.then(() => setPlaying(true)).catch(() => {});
      } else {
        setPlaying(true);
      }
    }
  }, []);

  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.muted = muted;
    }
  }, [muted]);

  const toggleMute = () => {
    setMuted((m) => !m);
  };

  return (
    <div style={{
      position: 'fixed',
      top: 18,
      right: 18,
      zIndex: 100,
      background: 'rgba(30,60,114,0.7)',
      borderRadius: '50%',
      padding: 10,
      boxShadow: '0 2px 8px #0003',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      cursor: 'pointer',
    }} onClick={toggleMute} aria-label={muted ? 'Unmute music' : 'Mute music'}>
      {muted ? <FaVolumeMute color="#fff" size={22} /> : <FaVolumeUp color="#fff" size={22} />}
      <audio ref={audioRef} src={MUSIC_URL} loop autoPlay style={{ display: 'none' }} />
    </div>
  );
}

export default BackgroundMusic;
